document.addEventListener("DOMContentLoaded", function () {
  // ... (existing code)

  // Event delegation for buttons
  const buttonsContainer = document.getElementById("buttons-container");
  buttonsContainer.addEventListener("click", function (event) {
    const targetId = event.target.id;

    switch (targetId) {
      case "add-contact-button":
        addContactModal.style.display = "block";
        console.log("rezq");
        break;
      case "add-opportunities-button":
        if (addOpportunitiesModal) {
          addOpportunitiesModal.style.display = "block";
        }
        break;
      case "add-project-button":
        if (addProjectModal) {
          addProjectModal.style.display = "block";
        }
        break;
      // Add more cases for other buttons as needed
    }
  });

  // Reusable function for closing modals
  function closeModal(modal) {
    if (modal) {
      modal.style.display = "none";
    }
  }

  // Close modal on button click
  closeModalContact.addEventListener("click", () => closeModal(addContactModal));
  closeModalProject.addEventListener("click", () => closeModal(addProjectModal));

  // Close modal on window click
  window.onclick = (event) => {
    if (event.target === addContactModal || event.target === addProjectModal) {
      closeModal(event.target);
    }
  };

  // ... (additional code)
});
```
